-- ----------------------------
-- Table structure for settings
-- ----------------------------
DROP TABLE IF EXISTS `settings`;
CREATE TABLE `settings` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `site_name` varchar(191) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '网站名称' COMMENT '网站名称',
  `key_words` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '网站关键字',
  `description` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '网站描述',
  `contactor` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '网站联系人',
  `mobile` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '手机联系方式',
  `tel` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '座机联系方式',
  `tel_tousu` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '投诉电话',
  `email` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '邮箱',
  `intro` longtext COLLATE utf8mb4_unicode_ci COMMENT '网站介绍',
  `address` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '网站地址',
  `logo` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '网站LOGO',
  `weixin` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '微信二维码',
  `qq` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT 'QQ联系方式',
  `post_page` int(11) NOT NULL DEFAULT '15' COMMENT '每页展示文章数量',
  `js_tongji` longtext COLLATE utf8mb4_unicode_ci COMMENT 'js统计代码',
  `beian` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '备案信息',
  `banquan` varchar(191) COLLATE utf8mb4_unicode_ci DEFAULT '' COMMENT '版权信息',
  `share` tinyint(4) NOT NULL DEFAULT '0' COMMENT '打开社会分享功能 0 不打开 1 打开',
  `yunlai` tinyint(4) NOT NULL DEFAULT '1' COMMENT '显示技术支持 0 不显示 1 显示',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  `deleted_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

